const initialState = {
	theme: {
		colors: {
			primary: '#141a20',
			secondary: '#ffffff',
			accent: '#17a2b8',
			divider: '#555d66'
		}
	},
	signals: []
};

const persistReducers = (state = initialState, action) => {
	switch (action.type) {
		case 'THEME': {
			return {
				...state,
				theme: action.payload,
			}
		}
		case 'SIGNALS': {
			return {
				...state,
				signals: action.payload,
			}
		}
		default: {
			return state;
		}
	}
};
// Exports
export default persistReducers;
