import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createStackNavigator } from '@react-navigation/stack';
import * as React from 'react';
import { Platform, StatusBar, StyleSheet, View, Text, Vibration, Image, TouchableOpacity } from 'react-native';
import { PersistGate } from 'redux-persist/integration/react';
import { Provider } from 'react-redux';
import { DefaultTheme, Provider as PaperProvider, Button, Avatar } from 'react-native-paper';
import { store, persistor } from './store';

import { Notifications } from 'expo';
import * as Permissions from 'expo-permissions';
import Constants from 'expo-constants';
import Ionicons from 'react-native-vector-icons/Ionicons';

import Wallet from './screens/Wallet'
import SettingsStack from './screens/SettingsStack'
import Exchange from './screens/Exchange'

const theme = {
	...DefaultTheme,
	colors: {
		...DefaultTheme.colors,
		primary: '#141a20',
		secondary: '#ffffff',
		accent: '#17a2b8',
		divider: '#555d66'
	}
};

const Tab = createBottomTabNavigator()
const Stack = createStackNavigator()
// 	Wallet: {
// 		screen: Home,
// 		navigationOptions: {
// 			tabBarIcon: ({ focused, color, size }) => {
// 				let iconName;

// 				if (route.name === 'Home') {
// 					iconName = 
// 				} 
// 				else if (route.name === 'Settings') {
// 					iconName = focused ? 'ios-list-box' : 'ios-list';
// 				}

// 				// You can return any component that you like here!
// 				return <Ionicons name={focused ? 'ios-information-circle' : 'ios-information-circle-outline';} size={size} color={color} />;
// 		}
// 	}
// });

const registerForPushNotificationsAsync = async () => {
	if (Constants.isDevice) {
		const { status: existingStatus } = await Permissions.getAsync(Permissions.NOTIFICATIONS);
		let finalStatus = existingStatus;
		if (existingStatus !== 'granted') {
			const { status } = await Permissions.askAsync(Permissions.NOTIFICATIONS);
			finalStatus = status;
		}
		if (finalStatus !== 'granted') {
			alert('Failed to get push token for push notification!');
			return;
		}
		token = await Notifications.getExpoPushTokenAsync();
		console.log(token)

		// fetch('https://algosats.ga/token', {
		// 	method: 'POST',
		// 	headers: {
		// 	Accept: 'application/json, text/plain, */*',
		// 	'Content-Type': 'application/json',
		// },
		// 	body: JSON.stringify({
		// 		token: token
		// 	}),
		// }).then((response) => response.json())
		// .then((responseJson) => {
		// })
		// .catch((error) => {
		// 	console.error(error);
		// });
	} 
	else {
		alert('Must use physical device for Push Notifications');
	}

	if (Platform.OS === 'android') {
		Notifications.createChannelAndroidAsync('signals', {
			name: 'Signals',
			sound: true,
			priority: 'max',
			vibrate: [0, 250, 250, 250],
		});
	}
};

export default function AppContainer(props) {
	const [expoPushToken, setExpoPushToken ] = React.useState('')
	const [notification, setNotification ] = React.useState({})

	const _handleNotification = notification => {
		//Vibration.vibrate();
		//console.log(notification);
		setNotification(notification)
	};

	React.useEffect(() => {
		registerForPushNotificationsAsync();
		// Handle notifications that are received or selected while the app
		// is open. If the app was closed and then opened by tapping the
		// notification (rather than just tapping the app icon to open it),
		// this function will fire on the next tick after the app starts
		// with the notification data.
		_notificationSubscription = Notifications.addListener(_handleNotification);
	},[])

	return (
		<Provider store={store}>
			<PersistGate loading={null} persistor={persistor}>
				<PaperProvider theme={theme}>
					<NavigationContainer>
						<Stack.Navigator 
							initialRouteName='Home'
							screenOptions={{
								headerShown: false
							}}
						>
							<Stack.Screen 
								name='Exchange' 
								component={Exchange} 
							/>
							<Stack.Screen 
								name='Home' 
								component={Home}
							/>
						</Stack.Navigator>
					</NavigationContainer>
				</PaperProvider>
			</PersistGate>
		</Provider>
	);
}

function Home(props) {
	const { navigation } = props
	return (
		<Tab.Navigator
			screenOptions={({ route }) => ({
				tabBarIcon: ({ focused, color, size }) => {

					if (route.name === 'Wallet') {
						return <Ionicons name='ios-wallet' size={size} color={color} />;
					} 
					else if (route.name === 'Settings') {
						return <Ionicons name='ios-settings' size={size} color={color} />;
					}
					else if (route.name === 'ExchangeButton') {
						return (
							<TouchableOpacity style={{activeOpacity: 0.8}} onPress={() => navigation.navigate('Exchange')}>
								<View style={{height: 90, width: 60, alignItems: 'center'}}>
									<Avatar.Icon style={{backgroundColor: theme.colors.accent}} size={60} icon="swap-horizontal-bold" />
								</View>
							</TouchableOpacity>
						)
					}
				},
			})}
			tabBarOptions={{
				borderTopWidth: 0,
				showLabel: false,
				activeTintColor: theme.colors.accent,
				inactiveTintColor: theme.colors.divider,
				style: {
					backgroundColor: theme.colors.primary,
					borderTopWidth: 0,
		            height: 55,
		            paddingBottom: 5,
				}
			}}
		>
			<Tab.Screen name="Wallet" component={Wallet}/>
			<Tab.Screen name="ExchangeButton" component={Wallet} tabBarButton />
			<Tab.Screen name="Settings" component={SettingsStack} />
		</Tab.Navigator>
	)
}
