import React from 'react'
import { Button, Text, Appbar, Divider, Avatar, ActivityIndicator } from 'react-native-paper';
import { StyleSheet, StatusBar, View, ScrollView, FlatList } from 'react-native';
import { useDispatch, useSelector } from "react-redux";
import { Notifications } from 'expo';

import { setTheme } from '.././actions.js'



const styles = StyleSheet.create({
	bottom: {
		position: 'absolute',
		left: 0,
		right: 0,
		top: 0,
	},
});

export default function Wallet(props) {
	const theme = useSelector(state => state.persistReducers.theme);
  	const dispatch = useDispatch();
  	const [balance, setBalance] = React.useState(1234.00)
  	const [amounts, setAmounts] = React.useState({
  		eth: 2.312000,
  		btc: 0.232144,
  		ltc: 5.232142
  	})

  	const [loading, setLoading] = React.useState(true)
	const { navigation } = props

	var style = {
		text: {
			color: theme.colors.secondary,
			fontSize: 40,
			paddingTop: 20,
		},
		button: {
			backgroundColor: theme.colors.accent,
		}
	}

	

	React.useEffect(() => {
		_notificationSubscription = Notifications.addListener(handleNotifications);
	}, [])

	handleNotifications = (notification) => {}
	return (
		<View style={{flex: 1, backgroundColor: theme.colors.primary}}>
			<StatusBar barStyle="light-content" backgroundColor={theme.colors.primary} />
			<Appbar.Header statusBarHeight={0} style={{justifyContent: 'center'}}>
				<Avatar.Image size={50} source={require('../assets/images/logo.png')}/>
			</Appbar.Header>
			<Divider style={{backgroundColor: theme.colors.divider}}/>
			<View style={{flex: 1, flexDirection: 'column'}}>
				<View style={{flexDirection: 'row', justifyContent: 'center'}}>
					<Text style={style.text}>$</Text>
					<Text style={style.text}>{balance.toFixed(2).toString().replace(/\B(?=(\d{3})+(?!\d))/g, ",")}</Text>
				</View>
				<View style={{flexDirection: 'row', justifyContent: 'center'}}>
					<View style={{paddingRight: 20, paddingTop: 10, paddingBottom: 20}}>
						<Button style={style.button} icon="arrow-down" mode="contained" onPress={() => console.log('Pressed')}>Deposit</Button>
					</View>
					<View style={{paddingTop: 10, paddingBottom: 20}}>
						<Button style={style.button} icon="arrow-up" mode="contained" onPress={() => console.log('Pressed')}>Withdraw</Button>
					</View>
				</View>
				<Divider style={{backgroundColor: theme.colors.divider}}/>
			</View>
			<Divider style={{backgroundColor: theme.colors.divider}}/>
		</View>
	)
}
